import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [currentPage, setCurrentPage] = React.useState('landing');
  return (
    <div className='App'>
      <nav className='navbar navbar-expand-lg navbar-light bg-light'>
        <a className='navbar-brand' href='#'>Loja do vitão</a>
        <div className='collapse navbar-collapse'>
          <ul className='navbar-nav ms-auto'>
            <li class name='nav-item'>
              <button className='nav-link btn'>Criar Conta</button>
            </li>
            <li class name='nav-item'>
              <button className='nav-link btn'>Login</button>
            </li>
            <li class name='nav-item'>
              <button className='nav-link btn'>Sair</button>
            </li>
          </ul>
        </div>
      </nav>
      {/*conteudo principal*/}
      <div className='container text-center mt-5'>
        {currentPage === 'landing'&& (
          <div className='mt-4'>
            <h1 className='display-4'>Bem-vindo a Loja do Vitão</h1>
          </div>
        )}
      </div>

      {/*criar conta*/}
      {currentPage === 'createAccount'&& (
          <div className='mt-4'>
            <div>add o form de criar conta</div>
          </div>
        )}

      {/*login*/}
      {currentPage === 'createAccount'&& (
          <div className='mt-4'>
            <div>add o form de login</div>
          </div>
        )}

      {/*sair*/}
      {currentPage === 'createAccount'&& (
          <div className='mt-4'>
            <div>add o form de sair</div>
          </div>
        )}

    </div>
  );
}

export default App;
