import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const UserAccountForm = ()=> {
  const[FormData, setFormData] = useState({
    userrname: '',
    email: '',
    password: '',
  });

  // Handle form input change
  const handleChange = (e) =>{
    console.log('Entrou aqui')
    const { name, value } = e.target;
    setFormData({
      ...FormData,
      [name]: value,
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      console.log('Salvando dados');
      const response = await axios.post('http://localhost:8080/users/novouser/', FormData);
      if (response.status === 200) {
        setResponseMessage('Conta criada com sucesso!');
      } else {
        setResponseMessage('Erro ao criar a conta de usuário.');
      }
    } catch (error) {
      setResponseMessage('Falha ao conectar ao servidor.');
    }
  };

  const [responseMessage, setResponseMessage] = useState('');

  return (
    <><nav class="navbar navbar-light bg-light">
      <span class="navbar-brand mb-0 h1">Loja do Vitão</span>
      <div class="d-flex flex-column flex-shrink-0 p-3 bg-light" style="width: 280px;">
        <ul class="nav nav-pills flex-column mb-auto">
          <li class="nav-item">
            <a href="#" class="nav-link active" aria-current="page">
              Home
            </a>
          </li>
          <li>
            <a href="#" class="nav-link link-dark">
              Dashboard
            </a>
          </li>
          <li>
            <a href="#" class="nav-link link-dark">
              Orders
            </a>
          </li>
          <li>
            <a href="#" class="nav-link link-dark">
              Products
            </a>
          </li>
          <li>
            <a href="#" class="nav-link link-dark">
              Customers
            </a>
          </li>
        </ul>
      </div>
    </nav><div className="user-account-form">
        <h3>Crie sua conta de usuário</h3>
        <form onSubmit={handleSubmit}>
          <div>
            <label>Nome:</label>
            <input
              type="text"
              name="username"
              value={FormData.username}
              onChange={handleChange}
              required />
          </div>
          <div>
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={FormData.email}
              onChange={handleChange}
              required />
          </div>
          <div>
            <label>Senha:</label>
            <input
              type="password"
              name="password"
              value={FormData.password}
              onChange={handleChange}
              required />
          </div>
          <button type="submit">Create Account</button>
        </form>
        <p>{responseMessage}</p>
      </div></>
  );
};

export default UserAccountForm;
