import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const EditProductForm = () => {
    const { id } = useParams(); // Obtém o ID do produto a partir da URL
    const [formData, setFormData] = useState({
        id: id,
        produto: '',
        descricao: '',
        preco: '',
        estoque: ''
    });

    const [responseMessage, setResponseMessage] = useState('');

    useEffect(() => {
        // Carrega os dados do produto ao montar o componente
        const fetchProduct = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/products/${id}`);
                setFormData(response.data);
            } catch (error) {
                setResponseMessage('Erro ao carregar os dados do produto');
            }
        };

        fetchProduct();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSave = async (e) => {
        e.preventDefault();
        try {
            console.log('Atualizando produto:', formData); // Log para depuração
            const response = await axios.put(`http://localhost:8080/products/updateProduct`, formData);
            console.log('Resposta da API:', response.data); // Log para depuração
            setResponseMessage(response.data.message || 'Produto atualizado com sucesso');
        } catch (error) {
            console.error('Erro ao atualizar o produto:', error); // Log para depuração
            setResponseMessage('Erro ao atualizar o produto');
        }
    };

    return (
        <div className='container mt-5'>
            <h3>Editar Produto</h3>
            <form onSubmit={handleSave}>
                <div className='form-group'>
                    <label>ID do Produto:</label>
                    <input
                        type='text'
                        name='id'
                        value={formData.id}
                        onChange={handleChange}
                        className='form-control'
                        readOnly
                    />
                </div>
                <div className='form-group'>
                    <label>Produto:</label>
                    <input
                        type='text'
                        name='produto'
                        value={formData.produto}
                        onChange={handleChange}
                        className='form-control'
                    />
                </div>
                <div className='form-group'>
                    <label>Descrição:</label>
                    <input
                        type='text'
                        name='descricao'
                        value={formData.descricao}
                        onChange={handleChange}
                        className='form-control'
                    />
                </div>
                <div className='form-group'>
                    <label>Preço:</label>
                    <input
                        type='text'
                        name='preco'
                        value={formData.preco}
                        onChange={handleChange}
                        className='form-control'
                    />
                </div>
                <div className='form-group'>
                    <label>Estoque:</label>
                    <input
                        type='text'
                        name='estoque'
                        value={formData.estoque}
                        onChange={handleChange}
                        className='form-control'
                    />
                </div>
                <button type='submit' className='btn btn-primary'>Salvar</button>
            </form>
            {responseMessage && <div className='alert alert-info mt-3'>{responseMessage}</div>}
        </div>
    );
};

export default EditProductForm;
