import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const DeleteProductForm = () => {
  const [productId, setProductId] = useState('');
  const [responseMessage, setResponseMessage] = useState('');

  const handleChange = (e) => {
    setProductId(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('jwtToken');
      const response = await axios.delete(`http://localhost:8080/products/delete/${productId}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      if (response.status === 200) {
        setResponseMessage('Produto deletado com sucesso!');
      } else {
        setResponseMessage('Erro ao deletar produto.');
      }
    } catch (error) {
      setResponseMessage('Falha ao conectar ao servidor.');
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <div className="user-account-form card p-4 shadow" style={{ maxWidth: '400px', width: '100%' }}>
        <h3 className="mb-4 text-center">Deletar Produto</h3>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>ID do Produto:</label>
            <input
              className="form-control"
              type="text"
              value={productId}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <button className="btn btn-danger w-100">Deletar</button>
          </div>
        </form>
        <div className="text-center">
          <p>{responseMessage}</p>
        </div>
      </div>
    </div>
  );
};

export default DeleteProductForm;
