import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const ProductDataForm = () => {
    const [formData, setFormData] = useState({
        id: '',
        produto: '',
        descricao: '',
        preco: '',
        estoque: ''
    });

    const [responseMessage, setResponseMessage] = useState('');
    const [products, setProducts] = useState([]); // Estado para armazenar os produtos

    // Tratar o evento change dos campos do form
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    // Tratar o salvar dados
    const handleSave = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('/api/products', formData);
            setResponseMessage(response.data.message);
            handleClear();
        } catch (error) {
            setResponseMessage('Erro ao salvar o produto');
        }
    };
    
    const handleClear = () => {
        setFormData({
            id: '',
            produto: '',
            descricao: '',
            preco: '',
            estoque: ''
        });
        setResponseMessage('');
    };

    const handleSearch = async () => {
        try {
            const response = await axios.get(`/api/products/${formData.id}`);
            setFormData(response.data);
            setResponseMessage('');
        } catch (error) {
            setResponseMessage('Produto nÃ£o encontrado');
        }
    };

    const handleListAll = async () => {
        try {
            const response = await axios.get('http://localhost:8080/products/getAllProducts');
            setProducts(response.data); // Armazena os produtos no estado
            setResponseMessage('');
        } catch (error) {
            setResponseMessage('Erro ao listar produtos');
        }
    }

    const handleNewProduct = async() => {
        try{
            const response = await axiox.post('https://localhost:8080/products/newProduct');
            setProducts(response.data);
            setResponseMessage('');
        } catch (error){
            setResponseMessage('Erro ao criar produto')

        }
    }

    return (
        <div className='user-account-form'>
            <h3>Cadastro de Produtos</h3>
            <form onSubmit={handleSave}>
                <div className='form-group'>
                    <label>ID:</label>
                    <input
                        type='text'
                        name='id'
                        value={formData.id}
                        onChange={handleChange}
                        className='form-control'
                    />
                </div>
                <div className='form-group'>
                    <label>Produto:</label>
                    <input
                        type='text'
                        name='produto'
                        value={formData.produto}
                        onChange={handleChange}
                        className='form-control'
                    />
                </div>
                <div className='form-group'>
                    <label>DescriÃ§Ã£o:</label>
                    <input
                        type='text'
                        name='descricao'
                        value={formData.descricao}
                        onChange={handleChange}
                        className='form-control'
                    />
                </div>
                <div className='form-group'>
                    <label>PreÃ§o:</label>
                    <input
                        type='text'
                        name='preco'
                        value={formData.preco}
                        onChange={handleChange}
                        className='form-control'
                    />
                </div>
                <div className='form-group'>
                    <label>Estoque:</label>
                    <input
                        type='text'
                        name='estoque'
                        value={formData.estoque}
                        onChange={handleChange}
                        className='form-control'
                    />
                </div>
                <button type='submit' className='btn btn-primary' onClick={handleSave}>Salvar</button>
                <button type='button' className='btn btn-secondary' onClick={handleClear}>Limpar</button>
                <button type='button' className='btn btn-info' onClick={handleSearch}>Buscar</button>
                <button type='button' className='btn btn-info' onClick={handleListAll}>Listar Todos</button>
            </form>
            {responseMessage && <div className='alert alert-info mt-3'>{responseMessage}</div>}
            <div className='mt-4'>
                <h4>Lista de Produtos</h4>
                <ul className='list-group'>
                    {products.map((product) => (
                        <li key={product.id} className='list-group-item'>
                            <strong>ID:</strong> {product.id} <br />
                            <strong>Produto:</strong> {product.produto} <br />
                            <strong>DescriÃ§Ã£o:</strong> {product.descricao} <br />
                            <strong>PreÃ§o:</strong> {product.preco} <br />
                            <strong>Estoque:</strong> {product.estoque}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default ProductDataForm;
