import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const CreateProductForm = () => {
    const [formData, setFormData] = useState({
        produto: '',
        descricao: '',
        preco: '',
        estoque: ''
    });

    const [responseMessage, setResponseMessage] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSave = async (e) => {
        e.preventDefault();
        try {
            console.log('Salvando produto:', formData); // Log para depuração
            const response = await axios.post('http://localhost:8080/products/newProduct', formData);
            console.log('Resposta da API:', response.data); // Log para depuração
            setResponseMessage(response.data.message || 'Produto salvo com sucesso');
            handleClear();
        } catch (error) {
            console.error('Erro ao salvar o produto:', error); // Log para depuração
            setResponseMessage('Erro ao salvar o produto');
        }
    };

    const handleClear = () => {
        setFormData({
            produto: '',
            descricao: '',
            preco: '',
            estoque: ''
        });
        setResponseMessage('');
    };

    return (
        <div className='container mt-5'>
            <h3>Cadastro de Produtos</h3>
            <form onSubmit={handleSave}>
                <div className='form-group'>
                    <label>Produto:</label>
                    <input
                        type='text'
                        name='produto'
                        value={formData.produto}
                        onChange={handleChange}
                        className='form-control'
                    />
                </div>
                <div className='form-group'>
                    <label>Descrição:</label>
                    <input
                        type='text'
                        name='descricao'
                        value={formData.descricao}
                        onChange={handleChange}
                        className='form-control'
                    />
                </div>
                <div className='form-group'>
                    <label>Preço:</label>
                    <input
                        type='text'
                        name='preco'
                        value={formData.preco}
                        onChange={handleChange}
                        className='form-control'
                    />
                </div>
                <div className='form-group'>
                    <label>Estoque:</label>
                    <input
                        type='text'
                        name='estoque'
                        value={formData.estoque}
                        onChange={handleChange}
                        className='form-control'
                    />
                </div>
                <button type='submit' className='btn btn-primary'>Salvar</button>
                <button type='button' className='btn btn-secondary' onClick={handleClear}>Limpar</button>
            </form>
            {responseMessage && <div className='alert alert-info mt-3'>{responseMessage}</div>}
        </div>
    );
};

export default CreateProductForm;
