import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

const ProductDataForm = () => {
    const [formData, setFormData] = useState({
        produto: '',
        descricao: '',
        preco: '',
        estoque: ''
    });

    const [responseMessage, setResponseMessage] = useState('');
    const [products, setProducts] = useState([]); // Estado para armazenar os produtos

    // Tratar o evento change dos campos do form
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    // Tratar o salvar dados
    const handleSave = async (e) => {
        e.preventDefault();
        try {
            console.log('Salvando produto:', formData); // Log para depuração
            const response = await axios.post('http://localhost:8080/products/newProduct', formData);
            console.log('Resposta da API:', response.data); // Log para depuração
            setResponseMessage(response.data.message || 'Produto salvo com sucesso');
            handleListAll(); // Atualiza a lista de produtos após salvar
        } catch (error) {
            console.error('Erro ao salvar o produto:', error); // Log para depuração
            setResponseMessage('Erro ao salvar o produto');
        }
    };

    const handleListAll = async () => {
        try {
            const response = await axios.get('http://localhost:8080/products/getAllProducts');
            setProducts(response.data); // Armazena os produtos no estado
            setResponseMessage('');
        } catch (error) {
            setResponseMessage('Erro ao listar produtos');
        }
    };

    return (
        <div className='user-account-form'>
            <h3>Lista de produtos</h3>
                <button type='button' className='btn btn-info' onClick={handleListAll}>Listar Todos</button>
            {responseMessage && <div className='alert alert-info mt-3'>{responseMessage}</div>}
            <div className='mt-4'>
                <h4>Lista de Produtos</h4>
                <ul className='list-group'>
                    {products.map((product) => (
                        <li key={product.id} className='list-group-item'>
                            <strong>ID:</strong> {product.id} <br />
                            <strong>Produto:</strong> {product.produto} <br />
                            <strong>Descrição:</strong> {product.descricao} <br />
                            <strong>Preço:</strong> {product.preco} <br />
                            <strong>Estoque:</strong> {product.estoque}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default ProductDataForm;
