import React, { useState } from 'react';
import axios from 'axios';

const UserAccountForm = ()=> {
  const[FormData, setFormData] = useState({
    userrname: '',
    email: '',
    password: '',
  });

  // Handle form input change
  const handleChange = (e) =>{
    console.log('Entrou aqui')
    const { name, value } = e.target;
    setFormData({
      ...FormData,
      [name]: value,
    });
  };

  // Handle form submission
  const handleSubmit = async (e) =>{
    e.preventDefault();

    try{
      console.log('Salvando dados');
      const response = await axios.post('https://www.postman.com/celebropensante/dsv/request/32e0m0l/novousuario')
    if(response.status === 200){
      setResponseMessage('Conta criada com sucesso!');
    }
    else{
      setResponseMessage('Erro ao criar a conta de usuário.');
    }  
    }catch(error) {
      setResponseMessage('Falha ao conectar ao servidor.');
    }
  };

  const [responseMessage, setResponseMessage] = useState('');

  return (
    <div className="user-account-form">
      <h3>Crie sua conta de usuário</h3>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nome:</label>
          <input
            type="text"
            name="username"
            value={FormData.username}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={FormData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Senha:</label>
          <input
            type="password"
            name="password"
            value={FormData.password}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Create Account</button>
      </form>
      <p>{responseMessage}</p>
    </div>
  );
};

export default UserAccountForm;
