import React from 'react';
import UserAccountForm from './UserAccountForm';
import ProductDataForm from './ProductDataForm';
import UserLogin from './UserLogin';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [currentPage, setCurrentPage] = React.useState('landing');

  const handleNavClick = (page) => {
    setCurrentPage(page);
  }
  return (
    <div className='App'>
      <nav className='navbar navbar-expand-lg navbar-light bg-light'>
        <a className='navbar-brand' href='#'>Loja do vitão</a>
        <div className='collapse navbar-collapse'>
          <ul className='navbar-nav ms-auto'>
            <li class name='nav-item'>
              <button className='nav-link btn' onClick={()=>handleNavClick('createAccount')}>Criar Conta</button>
            </li>
            <li class name='nav-item'>
              <button className='nav-link btn' onClick={()=> handleNavClick('login')}>Login</button>
            </li>
            <li class name='nav-item'>
              <button className='nav-link btn' onClick={()=>handleNavClick('logout')}>Sair</button>
            </li>
            <li class name='nav-item'>
              <button className='nav-link btn' onClick={()=>handleNavClick('logout')}>Produtos</button>
            </li>
          </ul>
        </div>
      </nav>
      {/*conteudo principal*/}
      <div className='container text-center mt-5'>
        {currentPage === 'landing'&& (
          <div className='mt-4'>
            <h1 className='display-4'>Bem-vindo a Loja do Vitão</h1>
          </div>
        )}

      {/*criar conta*/}
      {currentPage === 'createAccount'&& (
          <div className='mt-4'>
            <UserAccountForm />
          </div>
        )}

      {/*login*/}
      {currentPage === 'login'&& (
          <div className='mt-4'>
            <UserLogin />
          </div>
        )}

      {/*logout*/}
      {currentPage === 'logout'&& (
          <div className='mt-4'>
            <div>add o form de sair</div>
          </div>
        )}

      {/*Produtos*/}
      {currentPage === 'products'&& (
          <div className='mt-4'>
            <ProductDataForm />
          </div>
        )}
    </div>  
    </div>
  );
}

export default App;
