import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const UserAccountForm = ()=> {
  const[FormData, setFormData] = useState({
    userrname: '',
    email: '',
    password: '',
  });

  // Handle form input change
  const handleChange = (e) =>{
    console.log('Entrou aqui')
    const { name, value } = e.target;
    setFormData({
      ...FormData,
      [name]: value,
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      console.log('Salvando dados');
      const response = await axios.post('http://localhost:8080/users/novouser/', FormData);
      if (response.status === 200) {
        setResponseMessage('Conta criada com sucesso!');
      } else {
        setResponseMessage('Erro ao criar a conta de usuário.');
      }
    } catch (error) {
      setResponseMessage('Falha ao conectar ao servidor.');
    }
  };

  const [responseMessage, setResponseMessage] = useState('');

  return (
    <>
      <meta charSet="utf-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1" />
      <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap@4.6.2/dist/css/bootstrap.min.css" />
      <script src="https://cdn.jsdelivr.net/npm/jquery@3.7.1/dist/jquery.slim.min.js"></script>
      <script src="https://cdn.jsdelivr.net/npm/popper.js@1.16.1/dist/umd/popper.min.js"></script>
      <script src="https://cdn.jsdelivr.net/npm/bootstrap@4.6.2/dist/js/bootstrap.bundle.min.js"></script>
      <div className="user-account-form">
        <h3>Crie sua conta de usuário</h3>
        <form onSubmit={handleSubmit}>
          <div>
            <label>Nome:</label>
            <input
              type="text"
              name="username"
              value={FormData.username}
              onChange={handleChange}
              required
            />
          </div>
          <div>
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={FormData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div class="jumbotron text-center">
            <label>Senha:</label>
            <input
              type="password"
              name="password"
              value={FormData.password}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit">Create Account</button>
        </form>
        <p>{responseMessage}</p>
      </div>
    </>
  );
};

export default UserAccountForm;
