import React, { useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

const UserAccountForm = ()=> {
  const[FormData, setFormData] = useState({
    userrname: '',
    email: '',
    password: '',
  });

  // Handle form input change
  const handleChange = (e) =>{
    console.log('Entrou aqui')
    const { name, value } = e.target;
    setFormData({
      ...FormData,
      [name]: value,
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
  
    try {
      console.log('Salvando dados');
      const response = await axios.post('http://localhost:8080/users/novouser/', FormData);
      if (response.status === 200) {
        setResponseMessage('Conta criada com sucesso!');
      } else {
        setResponseMessage('Erro ao criar a conta de usuário.');
      }
    } catch (error) {
      setResponseMessage('Falha ao conectar ao servidor.');
    }
  };

  const [responseMessage, setResponseMessage] = useState('');

  return (
  <div>
    <header class="navbar navbar-light bg-light px-3">
      <div>
        <h2>Loja do Vitão</h2>
      </div>
      <div>
        <a href="#" class="text-decoration-none text-reset px-2">Criar conta</a>
        <a href="#" class="text-decoration-none text-reset px-2">Login</a>
        <a href="#" class="text-decoration-none text-reset px-2">Logout</a>
      </div>
    </header>
    <body class="bg-danger">
    <div className="user-account-form ">
      <div class="container">
        <div class="row">
        <h3>Crie sua conta de usuário</h3>
        <form onSubmit={handleSubmit}>
          <div>
            <label>Nome:</label>
            <input
              type="text"
              name="username"
              value={FormData.username}
              onChange={handleChange}
              required />
          </div>
          <div>
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={FormData.email}
              onChange={handleChange}
              required />
          </div>
          <div>
            <label>Senha:</label>
            <input
              type="password"
              name="password"
              value={FormData.password}
              onChange={handleChange}
              required />
          </div>
          <button type="submit">Create Account</button>
        </form>
        <p>{responseMessage}</p>
        </div>
      </div>
      </div>
      </body>
      </div>
  );
};

export default UserAccountForm;
